import {WalletDriver_Ethereum} from "@/utils/wallet/WalletDriver_Ethereum";
import {MetaMaskInpageProvider} from "@metamask/providers";

/*
    References:
        https://docs.metamask.io/guide/ethereum-provider.html#table-of-contents
        https://github.com/MetaMask/metamask-extension
        https://github.com/MetaMask/metamask-extension/pull/4606
        https://github.com/estebanmino/EIPs/blob/master/EIPS/eip-747.md

 */

export class WalletDriver_Metamask extends WalletDriver_Ethereum {

    private metamaskProvider: MetaMaskInpageProvider|null = null

    //
    // Public
    //

    public constructor() {
        super("Metamask",
            "https://freelogopng.com/images/all_img/1683020860metamask-logo-white.png",
            "data:image/png;base64,")
    }

    //
    // WalletDriver_Ethereum
    //

    public async isExpectedProvider(provider: object): Promise<boolean> {
        const result = "isMetaMask" in provider && provider.isMetaMask == true && !("isBraveWallet" in provider)
        return Promise.resolve(result)
    }

    public async connect(network: string): Promise<string[]> {
        const result = await super.connect(network)
        this.metamaskProvider?.once('chainChanged', this.handleDisconnect)
        return Promise.resolve(result)
    }

    public async disconnect(): Promise<void> {
        this.metamaskProvider?.off("chainChanged", this.handleDisconnect)
        this.metamaskProvider = null
        return super.disconnect()
    }

    //
    // Private
    //

    private readonly handleDisconnect = () => this.disconnect()


}
